import React, { useState } from 'react';

const Home = (props: { name: string }) => {
    // const [name, setName] = useState(null); // Set to null or provide a name value
    const [image, SetImage] = useState('')

    const handleUpload = () => {
        // Replace with your API endpoint for file upload
        fetch('http://localhost:8000/api/auth/upload', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ /* your upload data */ }),
        })
        .then(response => {
        if (response.ok) {
            // Handle success
            console.log('File uploaded successfully');
        } else {
            // Handle error
            console.error('Failed to upload file');
        }
        })
        .catch(error => {
            // Handle network error
            console.error('Network error', error);
        });
    };

    const handleDownload = () => {
        // Implement the API call to download here
        console.log('Downloading...');
        // You can use fetch or any other library to make the API call
    };

    const handleImage = (e: { target: { files: any; }; }) => {
        console.log(e.target.files)
        SetImage(e.target.files[0])
        console.log(image)
    }
    const handleApi = () => {
        const formData = new FormData()
        formData.append('image', image)
        console.log(formData)
        fetch('http://localhost:8000/api/auth/upload', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({formData}),
        })
    }

    return (
        <div>
            {props.name ? 
                (
                    <div>
                        // If Authenticated
                        <input type="file" id="myFile" name="filename" onChange={handleImage}/>
                        {/* <input type="submit" /> */}
                        <button onClick={handleApi}>Submit</button>
                    </div>
                )
                :
                (
                    // If not Authenticate
                    'You are not logged in'
                )
            }
        </div>
    );
}

export default Home;

